import React from 'react';
import Start from './Start';
import DayInputs from'./DayInputs';
import Outcome from './Outcome';
import { database } from 'firebase';

class App extends React.Component {

    state = {
        lastDate: ''
    }

    addLastDate = (date) => {
        this.setState({
            lastDate: date,
            dates: []
        })
    }

    updateDates = (dates) => {
        this.setState({
            dates
        })
    }

    render() {
        return (
            <>
            <h2>Czy nie biegam za dużo?</h2>
            <Start history={this.props.history} addLastDate={this.addLastDate} lastDate={this.state.lastDate}/>/
            <footer>
                <a href="mailto:przemoszadkowski@o2.pl">&copy;Przemysław Szadkowski</a>
            </footer>
            </>
        )
    }
}

export default App;
