import React from 'react';

class DayInputs extends React.Component {

    handleClick = () => {
        const inputValues = [...document.querySelectorAll('input')]
        .map(input => parseInt(input.value));
        const kilometers = inputValues
        .filter(value => value > 0)
        .reduce((x, y) => x + y);
        this.props.updateRate(kilometers);

        const lastWeek = inputValues.slice(21, inputValues.length - 1);
        console.log(lastWeek);
        // const lastWeek = 
        // proptypsy na inputach
        // wyliczenia z poszczególnych (ostatni) tygodni
        // odpowiedni kolor i opis do ratesa
    }

    render() {
        return(
            <>
                <div className="formArea">
                <p>
                    Lorem ipsum dolor sit amet consectetur adipisicing elit. Libero
                    inventore blanditiis earum optio corporis neque consequatur ab illum
                    harum atque, doloremque suscipit perspiciatis iste sed id, quasi omnis
                    vitae exercitationem.
                </p>
                <form>
                    {(this.props.dates).map(date =>
                        <input type="number" placeholder={date}></input>
                    )}
                </form>
                <button onClick={this.handleClick}>Oblicz!</button>
                </div>
                </>
        )
    }
}

export default DayInputs;
