import React from 'react';
import Start from './Start';

class App extends React.Component {

    state = {
        setDate: false,
        outcomeReady: false,
        lastDate: '',
        // dates: [],
        dates: {
            days: [],
            values: {}
        },
        rate: 0,
        color: '',
        // values: {}
    }

    addLastDate = (date) => {
        this.setState({
            lastDate: date
        })
    }

    // updateDates = (days) => {
    //     this.setState({
    //         dates: days
    //     })
    // }

    // updateDates = (days) => {
    //     const dates = {...this.state.dates};
    //     dates.days = days;
    //     this.setState({
    //         dates
    //     })
    // }

    updateDates = (days) => {
        const dates = {...this.state.dates};
        dates.days = days;
        dates.values[days] = '';
        this.setState({
            dates
        })
    }

    updateSetDate = (bool) => {
        this.setState({
            setDate: bool
        })
    }

    updateRate = rate => {
        this.setState({
            rate
        })
    }

    updateOutcome = bool => {
        this.setState({
            outcomeReady: bool
        })
    }

    updateColor = color => {
        this.setState({
            color
        })
    }

    updateValues = values => {
        const dates = {...this.state.dates};
        dates.values = values;
        this.setState({
            dates
        })
    }

    updateOneValue = (key, value) => {
        const dates = {...this.state.dates};
        dates.values[key] = value
        this.setState({
            dates
        })
    }

    render() {
        return (
            <>
            <h2>Czy biegam za dużo?</h2>
            <Start history={this.props.history} addLastDate={this.addLastDate} lastDate={this.state.lastDate} updateDates={this.updateDates} updateSetDate={this.updateSetDate} isSetDate={this.state.setDate} dates={this.state.dates} updateRate={this.updateRate} isOutcomeReady={this.state.outcomeReady} updateOutcome={this.updateOutcome} rate={this.state.rate} updateColor={this.updateColor} color={this.state.color} updateValues={this.updateValues} updateOneValue={this.updateOneValue}/>
            <footer>
                <a href="mailto:przemoszadkowski@o2.pl">&copy;Przemysław Szadkowski</a>
            </footer>
            </>
        )
    }
}

export default App;
