import React from 'react';
import Start from './Start';
import DayInputs from'./DayInputs';
import Outcome from './Outcome';
import { database } from 'firebase';

class App extends React.Component {

    state = {
        setDate: false,
        lastDate: '',
        dates: [],
        rate: 0
    }

    addLastDate = (date) => {
        this.setState({
            lastDate: date
        })
    }

    updateDates = (days) => {
        this.setState({
            dates: days
        })
    }

    updateSetDate = (bool) => {
        this.setState({
            setDate: bool
        })
    }

    updateRate = rate => {
        this.setState({
            rate
        })
    }

    render() {
        return (
            <>
            <h2>Czy nie biegam za dużo?</h2>
            <Start history={this.props.history} addLastDate={this.addLastDate} lastDate={this.state.lastDate} updateDates={this.updateDates} updateSetDate={this.updateSetDate} isSetDate={this.state.setDate} dates={this.state.dates} updateRate={this.updateRate}/>/
            <footer>
                <a href="mailto:przemoszadkowski@o2.pl">&copy;Przemysław Szadkowski</a>
            </footer>
            </>
        )
    }
}

export default App;
